-- Remove any existing data
DELETE FROM [CARDPAYMENTRESULT]
DELETE FROM [CARDPAYMENTENTRY]
DELETE FROM [COUNTRYORREGIONTRANSLATION]
DELETE FROM [COUNTRYORREGION]

-- COUNTRYORREGION table data

-- COUNTRYORREGIONTRANSLATION table data

USE msdb

-- Delete the history purge job if it already exists
DECLARE @jobId binary(16)
SELECT @jobId = job_id FROM msdb.dbo.sysjobs WHERE (name = N'cardpaymentacceptconnect_job_purge_history')
IF (@jobId IS NOT NULL)
BEGIN
    EXEC msdb.dbo.sp_delete_job @jobId
END

-- Create a job for history purge
EXEC [dbo].[sp_add_job]
    @job_name = N'cardpaymentacceptconnect_job_purge_history',
	@owner_login_name = N'sa';
GO

-- Create a job step to call a stored procedure to purge history
-- In the example, the entry life is set to 1440 minutes (24 hours).  
-- Change entry life as needed. Do not make it shorter than the expiration period.
EXEC [dbo].[sp_add_jobstep]
    @job_name = N'cardpaymentacceptconnect_job_purge_history',
    @step_name = N'cardpaymentacceptconnect_jobstep_run_sp_purgecardpaymenthistory',
    @subsystem = N'TSQL',
    @command = N'EXEC [CardPaymentAcceptConnect].[dbo].[PURGECARDPAYMENTHISTORY] @i_EntryLifeInMinutes = 1440;', 
    @retry_attempts = 3,
    @retry_interval = 5;
GO

-- Delete the nightly schedule if it already exists
IF EXISTS (SELECT '*' FROM msdb.dbo.sysschedules where name = N'cardpaymentacceptconnect_schedule_nightly')
BEGIN
	EXEC sp_delete_schedule @schedule_name = N'cardpaymentacceptconnect_schedule_nightly'
END

-- Create a schedule to run nightly at 1AM
EXEC [dbo].[sp_add_schedule]
    @schedule_name = N'cardpaymentacceptconnect_schedule_nightly',
    @freq_type = 4,
    @freq_interval = 1,
    @active_start_time = 010000,
	@owner_login_name = N'sa';
GO

-- Attach the schedule to the job
EXEC [dbo].[sp_attach_schedule]
   @job_name = N'cardpaymentacceptconnect_job_purge_history',
   @schedule_name = N'cardpaymentacceptconnect_schedule_nightly';
GO

-- Target the job to the local server
EXEC [dbo].[sp_add_jobserver]
    @job_name = N'cardpaymentacceptconnect_job_purge_history';
GO